import json
import httpx
from uuid import uuid4
from langflow.custom import Component
from langflow.io import MessageTextInput, Output
from langflow.schema import Data

class MedicalAppointmentComponent(Component):
    display_name = "Medical Appointment API"
    description = "Creates medical appointments by parsing JSON data and sending POST requests to the API"
    documentation: str = "https://docs.langflow.org/components-custom-components"
    icon = "calendar"
    name = "MedicalAppointmentComponent"
    
    inputs = [
        MessageTextInput(
            name="json_data",
            display_name="JSON Appointment Data",
            info="JSON data containing appointment information",
            value="{}",
            tool_mode=True,
        ),
    ]
    
    outputs = [
        Output(display_name="API Response", name="output", method="build_output"),
    ]
    
    def build_output(self) -> Data:
        # API configuration
        function_root_url = "https://adgbushwhikrixthtimc.supabase.co/functions/v1/index"
        headers = {
            "Authorization": "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImFkZ2J1c2h3aGlrcml4dGh0aW1jIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTc3NzMyMjcsImV4cCI6MjA3MzM0OTIyN30.mDGwtotBGSVhWZswActA6l55vgmfN9u7HVMx7uM03YA",
            "Content-Type": "application/json"
        }
        
        try:
            # Parse the input JSON data
            if isinstance(self.json_data, str):
                # Clean the input string - remove code blocks, extra whitespace
                cleaned_json = self.json_data.strip()
                
                # Remove markdown code block markers if present
                if cleaned_json.startswith('```json'):
                    cleaned_json = cleaned_json[7:]  # Remove ```json
                if cleaned_json.startswith('```'):
                    cleaned_json = cleaned_json[3:]   # Remove ```
                if cleaned_json.endswith('```'):
                    cleaned_json = cleaned_json[:-3]  # Remove trailing ```
                
                # Strip again after removing markers
                cleaned_json = cleaned_json.strip()
                
                # Debug: Log the cleaned JSON for troubleshooting
                print(f"Cleaned JSON input: {repr(cleaned_json)}")
                
                appointment_data = json.loads(cleaned_json)
            else:
                appointment_data = self.json_data
            
            # Validate required fields
            required_fields = ["patientname", "doctorname", "date", "time"]
            missing_fields = [field for field in required_fields if field not in appointment_data]
            
            if missing_fields:
                error_msg = f"Missing required fields: {', '.join(missing_fields)}"
                self.status = Data(value={"error": error_msg, "success": False})
                return Data(value={"error": error_msg, "success": False})
            
            # Always generate a new appointment ID
            appointment_data["appointmentid"] = str(uuid4())
            
            # Make the POST request
            post_response = httpx.post(
                f"{function_root_url}/appointments", 
                headers=headers, 
                json=appointment_data, 
                timeout=10.0
            )
            
            # Check for HTTP errors
            post_response.raise_for_status()
            
            # Parse response
            created_appointment = post_response.json()
            
            result = {
                "success": True,
                "status_code": post_response.status_code,
                "appointment_data": created_appointment,
                "message": "Appointment created successfully"
            }
            
            self.status = Data(value=result)
            return Data(value=result)
            
        except json.JSONDecodeError as e:
            error_msg = f"Invalid JSON format: {str(e)}. Input received: {repr(self.json_data[:100])}..."
            result = {"error": error_msg, "success": False}
            self.status = Data(value=result)
            return Data(value=result)
            
        except httpx.HTTPStatusError as e:
            error_msg = f"HTTP Error {e.response.status_code}: {e.response.text}"
            result = {
                "error": error_msg, 
                "success": False,
                "status_code": e.response.status_code,
                "response_body": e.response.text
            }
            self.status = Data(value=result)
            return Data(value=result)
            
        except httpx.TimeoutException:
            error_msg = "Request timed out after 10 seconds"
            result = {"error": error_msg, "success": False}
            self.status = Data(value=result)
            return Data(value=result)
            
        except httpx.RequestError as e:
            error_msg = f"Network error: {str(e)}"
            result = {"error": error_msg, "success": False}
            self.status = Data(value=result)
            return Data(value=result)
            
        except Exception as e:
            error_msg = f"Unexpected error: {str(e)}"
            result = {"error": error_msg, "success": False}
            self.status = Data(value=result)
            return Data(value=result)

